import React, { useEffect, useState } from 'react';
import { createItem, getItems } from '../../functions';

const UploadFile = () => {
  const [title, setTitle] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [items, setItems] = useState([]);

  const onSubmitHandler = async (e) => {
    e.preventDefault();
    if (!title || !selectedFile) {
      return;
    }
    const formData = new FormData();
    formData.append('title', title);
    formData.append('image', selectedFile);

    try {
      const result = await createItem(formData);
      setItems([...items, result]);
      setTitle('');
      setSelectedFile(null);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await getItems();
        console.log('fetch data', result);
        setItems(result);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, []);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  return (
    <div className="container">
      <form action="" onSubmit={onSubmitHandler}>
        <input
          type="text"
          className="input-field"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <input
          type="file"
          accept="image/*"
          onChange={handleFileChange}
        />
        <div className="right-align">
          <button className="btn" type="submit">Submit</button>
        </div>
      </form>
      {items?.map((item) => (
        <div className="card" key={item._id}>
          <div className="card-image waves-effect waves-block waves-light">
            <img className="activator" style={{ width: '100%', height: 300 }} src={`http://localhost:5000/uploads/${item.image}`} alt="item" />
          </div>
          <div className="card-content">
            <span className="card-title activator grey-text text-darken-4">{item.title}</span>
          </div>
        </div>
      ))}
    </div>
  );
};

export default UploadFile;
