import React, { useEffect, useState } from 'react';
import FileBase from 'react-file-base64'; // Import the correct component name
import { createItem, getItems } from '../../functions';

const UploadFile = () => {
  const [title, setTitle] = useState('');
  const [selectedImage, setSelectedImage] = useState(null);
  const [items, setItems] = useState([]);

  const onSubmitHandler = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('title', title);
    formData.append('image', selectedImage);

    const result = await createItem(formData);
    setItems([...items, result]);
    setTitle('');
    setSelectedImage(null);
  };

  useEffect(() => {
    const fetchData = async () => {
      const result = await getItems();
      console.log('fetch data', result);
      setItems(result);
    };
    fetchData();
  }, []);

  return (
    <div className="container">
      <form action="" onSubmit={onSubmitHandler}>
        <input
          type="text"
          className="input-field"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <FileBase
          type="file"
          multiple={false}
          onDone={({ base64 }) => setSelectedImage(base64)}
        />
        <div className="right-align">
          <button className="btn">submit</button>
        </div>
      </form>
      {items?.map((item) => (
        <div className="card" key={item._id}>
          <div className="card-image waves-effect waves-block waves-light">
            <img className="activator" style={{ width: '100%', height: 300 }} src={item.image} alt="item" />
          </div>
          <div className="card-content">
            <span className="card-title activator grey-text text-darken-4">{item.title}</span>
          </div>
        </div>
      ))}
    </div>
  );
};

export default UploadFile;
